mapper.select:
SELECT user_id, password_hash, name, email, dept_id, position, status, hire_date
FROM employees
WHERE user_id = #{userId}

mapper.update:
UPDATE employees
SET name=#{name}, email=#{email}, dept_id=#{deptId}, position=#{position},
status=#{status}, hire_date=#{hireDate}
WHERE user_id=#{userId}
